import{_ as t,c as d,o as e,V as r}from"./chunks/framework.__fMkWnK.js";const b=JSON.parse('{"title":"CSS单位","description":"","frontmatter":{},"headers":[],"relativePath":"guide/css/css单位.md","filePath":"guide/css/css单位.md","lastUpdated":1732283740000}'),o={name:"guide/css/css单位.md"},c=r('<h1 id="css单位" tabindex="-1">CSS单位 <a class="header-anchor" href="#css单位" aria-label="Permalink to &quot;CSS单位&quot;">​</a></h1><h2 id="绝对单位" tabindex="-1">绝对单位 <a class="header-anchor" href="#绝对单位" aria-label="Permalink to &quot;绝对单位&quot;">​</a></h2><p>绝对单位是固定的，不受环境因素（如父元素、视口大小）的影响。它们在不同的浏览器和设备上总是相同的。</p><table><thead><tr><th>单位</th><th>释义</th><th>描述</th></tr></thead><tbody><tr><td>px</td><td>像素</td><td>最常用的绝对单位之一<br><code>1px</code> 等于一个设备像素，适用于精确控制尺寸</td></tr><tr><td>pt</td><td>点</td><td>通常用于印刷领域<br><code>1pt</code> 等于 <code>1/72</code> 英寸（约 0.353 mm）</td></tr><tr><td>pc</td><td>百分</td><td><code>1pc = 12pt</code></td></tr><tr><td>in</td><td>英寸</td><td><code>1in = 2.54cm</code></td></tr><tr><td>cm</td><td>厘米</td><td><code>1cm = 10mm</code></td></tr><tr><td>mm</td><td>毫米</td><td><code>1mm = 0.1cm</code></td></tr></tbody></table><h2 id="相对单位" tabindex="-1">相对单位 <a class="header-anchor" href="#相对单位" aria-label="Permalink to &quot;相对单位&quot;">​</a></h2><p>相对单位的值是相对的，它们依赖于某些参考值，如父元素的大小、视口的大小、根元素的字体大小等。</p><p>这些单位通常用于响应式设计，因为它们可以根据环境条件自动调整。</p><table><thead><tr><th>单位</th><th>释义</th><th>描述</th></tr></thead><tbody><tr><td>em</td><td></td><td>当对于当前父元素的字体大小<br>如果当前父元素的字体大小是 <code>16px</code>，那么 <code>1em = 16px</code></td></tr><tr><td>rem</td><td>root em</td><td>相对于根元素（通常是 <code>html</code>）的字体大小<br>在 root 元素的字体大小为 <code>16px</code> 的情况下，<code>1 rem = 16px</code></td></tr><tr><td>%</td><td>百分比</td><td>相对于父元素的大小<br>常用于宽度、高度、边距和填充等属性</td></tr><tr><td>vw</td><td>视口宽度</td><td>相对于视口宽度的 <code>1%</code><br><code>100vw</code> 等于视口的宽度</td></tr><tr><td>vh</td><td>视口高度</td><td>相对于视口高度的 <code>1%</code><br><code>100vh</code> 等于视口的高度</td></tr><tr><td>vmin</td><td>视口最小值</td><td>相对于视口宽度和高度中较小的那个值的 <code>1%</code></td></tr><tr><td>vmax</td><td>视口最大值</td><td>相对于视口宽度和高度中较大的那个值的 <code>1%</code></td></tr><tr><td>ex</td><td></td><td>相对于当前元素的字体*高度<br>通常用于字体的高度相关属性</td></tr><tr><td>ch</td><td></td><td>相对于当前字体的 <code>0</code> 字符的宽度<br>通常用于设置字符基准的宽度</td></tr></tbody></table>',8),a=[c];function s(h,m,i,n,p,_){return e(),d("div",null,a)}const u=t(o,[["render",s]]);export{b as __pageData,u as default};
